$(function(){
	$("#confirm").dialog({
		autoOpen: false
	});

	$("input[name='confirm']").on("click",function(a) {
		if($("#action option[value='delete']").is(":selected")){
			a.preventDefault();
			$("#confirm").dialog({
				buttons: {
					Confirmer: function() {
		                $(this).dialog("close"), $(".post_action").submit();
		            },
		            Annuler: function() {
		                $(this).dialog("close");
		            }
		        }
			});
			$("#confirm").dialog("open");
		}
	});
});

$(".form-choice").submit(function(a) {
    return !!$("input[value='true']").is(":checked") || (alert("La question doit avoir au moins une r√©ponse vraie"), !1)
});

$(function(){
	var checkboxes = $(".form-check-input"),
	    submitButt = $("input[name='confirm']");

	checkboxes.click(function() {
	    submitButt.attr("disabled", !checkboxes.is(":checked"));
	});
});